using System.Text.RegularExpressions;
using WebsiteTextExtractor.Core.Services;
using Xunit;

namespace WebsiteTextExtractor.Core.Tests.Services
{
    public class TranslateServiceTests
    {
        private readonly TranslatePageService _translateService;

        public TranslateServiceTests() => _translateService = new TranslatePageService();

        [Fact]
        public async Task Test()
        {
            // Arrange
            string input = @"[[[""Capítulo 1: De hoje em diante, sou um príncipe real\n"",""Chapter 1: From today onwards, I am a Royal Prince\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan podia sentir que alguém o estava chamando.\n\n\n"",""Cheng Yan could sense that someone was calling him.\n\n\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""“Sua Alteza, por favor, acorde…”\n"",""“Your Highness, please wake up…”\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Ele virou a cabeça, mas os sons que ouviu não desapareceram, na verdade, eles ficaram ainda mais altos. "",""He turned his head away, but the sounds he’d heard didn’t disappear, they actually proceeded to get even louder instead."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Então, ele sentiu alguém puxar suavemente sua manga.\n"",""Then, he felt someone gently tug on his sleeve.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""\""Sua Alteza, meu Príncipe Real!\""\n"",""“Your Highness, my Royal Prince!”\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Os olhos de Cheng Yan se abriram. "",""Cheng Yan’s eyes snapped open."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Seu ambiente familiar havia desaparecido, sua mesa de trabalho havia sumido e as paredes familiares cheias de post-its haviam desaparecido. "",""His familiar surroundings had disappeared, his work desk was gone, and the familiar walls filled with post-its were gone."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Todos eles foram substituídos por uma paisagem estranha. "",""They’d all been replaced by a strange landscape."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Uma praça pública redonda cercada por pequenas casas de tijolos e a forca erguida no centro da praça agora dominavam seu campo de visão. "",""A round public square that was enclosed by small brick houses, and the gallows that were erected in the center of the square now dominated his field of view."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele próprio estava sentado a uma mesa do outro lado da praça da forca. "",""He himself sat at a table across the square from the gallows."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Não havia uma cadeira de escritório giratória macia sob sua bunda, mas uma cadeira de ferro dura e fria. "",""There wasn’t a soft rotating office chair under his butt, but a cold hard iron chair instead."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Havia também um grupo de pessoas sentadas com ele e observando-o atentamente. "",""There was also a group of people sitting with him and watching him intently."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Vários deles estavam vestidos como senhores e senhoras medievais daqueles filmes de faroeste e tentavam conter o riso.\n"",""Several of them were dressed as medieval lords and ladies from those Western flicks, and were trying to suppress their giggles.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Que diabos? "",""What the hell?"",null,null,10],[""Eu não estava apenas correndo para terminar meus projetos mecânicos antes do prazo? "",""Wasn’t I just rushing to finish my mechanical blueprints before the deadline?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Cheng Yan estava perdido enquanto pensava consigo mesmo. "",""Cheng Yan was at a loss as he thought to himself."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Por três dias consecutivos, ele vinha fazendo hora extra. "",""For three consecutive days, he had been working overtime."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Assim, ele estava mentalmente e fisicamente no seu limite. "",""Thus, he was both mentally and physically at his limit."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele só conseguia se lembrar vagamente de que seu batimento cardíaco ficou instável e que ele só queria se deitar em sua mesa e fazer uma pausa...\n"",""He could only vaguely remember that his heartbeat had become unsteady, and that he’d just wanted to lie down on his desk and take a break…\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""\""Sua Alteza, por favor, declare sua decisão.\""\n"",""“Your Highness, please declare your ruling.”\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""O orador foi aquele que secretamente puxou sua manga. "",""The speaker was the one that had secretly tugged on his sleeve."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Seu rosto era velho, aparentemente na casa dos cinquenta ou sessenta anos, e ele usava uma túnica branca. "",""His face was old, seemingly in his fifties or sixties, and he wore a white robe."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""À primeira vista, ele se parecia um pouco com Gandalf, de O Senhor dos Anéis.\n"",""At first glance, he looked a bit like Gandalf, from The Lord of the Rings.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Estou sonhando? "",""Am I dreaming?"",null,null,1],[""Cheng Yan pensou enquanto lambia os lábios secos, Decisão? "",""Cheng Yan thought as he licked his dry lips, Ruling?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Que decisão?\n"",""What ruling?\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Quando ele rapidamente olhou ao redor, sua confusão foi varrida. "",""As he quickly glanced around, his confusion was swept away."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""As pessoas que o rodeavam olhavam todas na direção do centro da praça, para a forca. "",""The people surrounding him were all looking in the direction of the center of the square, at the gallows."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Muitas pessoas da cidade também estavam na praça e agitavam os punhos enquanto gritavam e até jogavam uma pedra ocasional na forca e na figura nela.\n"",""Many townspeople were also in the plaza and were waving their fists while they shouted and even threw an occasional stone towards the gallows and the figure on it.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan só tinha visto um instrumento de morte tão antigo em filmes. "",""Cheng Yan had only ever seen such an ancient instrument of death in movies."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""A forca consistia em dois pilares que se estendiam para cima cerca de 4 metros a partir de uma base elevada, com uma viga que se estendia entre os dois pilares com uma corda de cânhamo amarela grossa ao redor do meio da viga. "",""The gallows consisted of two pillars extending upwards about 4 meters from a raised base, with a crossbeam extending between the two pillars with a thick yellow hemp rope around the middle of the crossbeam."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Uma ponta da corda foi amarrada à forca e a outra ponta foi amarrada em um laço em volta do pescoço do prisioneiro.\n\n\n"",""One end of the rope was tied to the gallows, and the other end was tied into a noose around a prisoner’s neck.\n\n\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Nesse estranho sonho em que Cheng Yan pensou estar, ele descobriu que era capaz de ver tudo claramente. "",""In this strange dream Cheng Yan thought he was in, he found that he was able to see everything clearly."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Normalmente, ele até precisaria usar os óculos para ver as palavras na tela do computador, mas agora Chen Yang podia ver cada detalhe da forca, que estava a cinquenta metros de distância, sem os óculos.\n"",""Usually, he’d even need to wear his glasses to see the words on a computer screen, but now Chen Yang could see every detail of the gallows, which were fifty meters away, without his glasses.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""O prisioneiro no alto da forca tinha a cabeça totalmente coberta por um capuz e as mãos amarradas nas costas. "",""The prisoner atop the gallows had their head completely covered with a hood and had their hands tied behind their back."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Eles usavam roupas cinza sujas que eram pouco mais que trapos enrolados em uma armação tão fina que parecia que você poderia facilmente envolver sua mão em torno de seu tornozelo exposto. "",""They wore dirty grey clothes that were little more than rags draped over a frame so thin, it seemed you could easily wrap your hand around their exposed ankle."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Cheng Yan julgou que a prisioneira era uma mulher por seu peito ligeiramente protuberante e observou enquanto ela ficava ali tremendo com o vento frio, mas ainda tentando se levantar para enfrentar seu destino de pé.\n"",""Cheng Yan judged the prisoner to be female by her faintly bulging chest, and looked on as she stood there shivering in the chilly wind, but still trying to stand up straight to face her fate on her feet.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Tudo bem então, Cheng Yan pensou consigo mesmo, que crime essa mulher cometeu que fez com que tantas pessoas ficassem tão indignadas e esperassem que ela fosse enforcada com tanta raiva e hostilidade?\n"",""Alright then, Cheng Yan thought to himself, what crime did this woman commit that caused so many people to be so outraged, and to wait for her to be hanged with such rage and hostility?\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""As memórias de Cheng Yan apareceram, quase como se tivessem sido ativadas de repente e ele percebeu a causa da situação e a resposta para sua pergunta quase ao mesmo tempo.\n"",""Cheng Yan’s memories appeared, almost as if they’d suddenly been turned on and he realized the cause of the situation, and the answer to his question, at almost the same time.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Ela era uma “bruxa”.\n"",""She was a “witch”.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Ela foi considerada como tendo caído na tentação do diabo e era conhecida como uma encarnação do mal.\n"",""She was considered to have fallen to the temptation of the devil and was known as an incarnation of evil.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""\""Sua Alteza?\"" "",""“Your Highness?”"",null,null,1],[""O sósia de Gandalf insistiu cautelosamente.\n"",""The Gandalf lookalike cautiously urged.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan olhou para o velho. "",""Cheng Yan glanced at the old man."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Bem, as novas memórias de Cheng Yan diziam a ele, o velho não se chamava Gandalf, seu nome verdadeiro era Barov, e ele era um Ministro Adjunto das Finanças enviado pelo pai de Roland para ajudar no governo do território.\n"",""Well, Cheng Yan’s new memories told him, the old man wasn’t called Gandalf, his real name was Barov, and he was an Assistant Minister of Finance dispatched by the Roland’s father to assist in the governing of the territory.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""A identidade de Cheng Yan era a do 4º Príncipe do Reino de Graycastle, Roland, e ele foi enviado aqui para governar esta região. "",""Cheng Yan’s identity was that of the 4th Prince of the Kingdom of Graycastle, Roland, and he had been sent here to govern this region."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Os moradores desta cidade fronteiriça capturaram e prenderam a bruxa, entregando-a imediatamente aos guardas locais para interrogatório. "",""The residents of this border town had caught and seized the witch, immediately turning her over to the local guards to question."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Questionando? "",""Questioning?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Não, ela foi imediatamente enviada para ser condenada sem nenhuma oportunidade de se defender. "",""No, She was immediately sent to be sentenced with no opportunity to defend herself."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""A execução de suspeitos de bruxaria geralmente era supervisionada pelos senhores ou bispos locais, mas desde que ele assumiu o controle deste território, emitir tais ordens tornou-se sua obrigação.\n"",""The execution of suspected witches was usually overseen by the local lords or bishops, but since he’d assumed control of this territory, issuing such orders had become his obligation.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""A memória de Cheng Yan respondia suas perguntas uma a uma, era desnecessário filtrá-las e lê-las, era como se sempre tivessem sido suas próprias experiências. "",""Cheng Yan’s memory answered his questions one by one, it was unnecessary to filter and read through them, it was as if they had always been his own experiences."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele ficou momentaneamente confuso, não havia como um sonho ter tantos detalhes. "",""He was momentarily confused, there was absolutely no way a dream could have so many details."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Então, Cheng Yan pensou, seria possível que isso não fosse um sonho? "",""Then, Cheng Yan thought, was it possible that this wasn’t a dream?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Eu realmente viajei no tempo, para a idade das trevas da Europa medieval, e me tornei Roland? "",""I’ve really traveled through time, to the dark ages of medieval Europe, and have become Roland?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Eu fui de um lamentável engenheiro mecânico com o nariz enfiado em seus papéis para um grande 4º Príncipe durante a noite?\n\n\n"",""I’ve gone from a pitiful mechanical engineer with his nose down in his papers to a grand 4th Prince overnight?\n\n\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Este pedaço de território que parecia tão árido e atrasado estava no Reino de Graycastle, um nome que ele nunca tinha visto em seus livros de história.\n"",""This piece of territory that looked so barren and backward was in the Kingdom of Graycastle, a name that he had never seen in his history books.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Bem, então como eu quero lidar com isso? "",""Well, then how do I want to handle this?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Cheng Yan pensou consigo mesmo.\n"",""Cheng Yan thought to himself.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan decidiu que tentaria examinar como uma coisa não científica como ser transportado no tempo e no espaço havia acontecido mais tarde, sua preocupação imediata era como impedir que a farsa acontecesse à sua frente. "",""Cheng Yan decided he would try and examine how an unscientific thing like being transported through time and space had happened later, his immediate concern was with how to stop the farce taking place in front of him."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Atribuir a culpa dos desastres e infortúnios que lhes sobrevieram a essas “bruxas” foi ato de bárbaros ignorantes. "",""Assigning the blame for the disasters and misfortune that befell them onto these “witches” was the act of ignorant barbarians."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele realmente não conseguia fazer nada tão estúpido quanto enforcar outra pessoa apenas para satisfazer as massas que assistiam.\n"",""He really couldn’t bring himself to do anything as stupid as hanging another person just to satisfy the watching masses.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Ele agarrou as ordens escritas formais mantidas por Barov e jogou-as no chão e disse lentamente: “Estou me sentindo cansado, daremos nosso julgamento outro dia. "",""He grabbed the formal written orders held by Barov and tossed them to the ground and slowly said, “I’m feeling tired, we will give our judgement another day."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Tribunal demitido, agora disperse as pessoas!”\n"",""Court dismissed, now disperse people!”\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan sabia que não podia arriscar ser imprudente, então vasculhou cuidadosamente suas memórias e refletiu o comportamento do ex-príncipe. "",""Cheng Yan knew he couldn’t risk being reckless, so he rummaged carefully through his memories and reflected the former prince’s behavior."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele teve que continuar com o dandismo e o comportamento malandro do ex-príncipe. "",""He had to continue on with the former prince’s dandyism and roguish behavior."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""É isso mesmo, o próprio quarto príncipe era confuso, tinha um caráter desagradável e fazia o que queria sem pensar nas consequências de suas ações. "",""That’s right, the fourth prince himself was messed up, had a nasty character, and did whatever he wanted with no thoughts to the consequences of his actions."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""De qualquer forma, Cheng Yan refletiu, eles poderiam realmente esperar que um jovem incontrolável de vinte e poucos anos tivesse um bom comportamento?\n"",""Anyways, Cheng Yan mused, could they really expect an uncontrollable twenty-something year old to have good behavior?\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Os membros da nobreza que se sentaram com ele mantiveram a equanimidade com sua declaração inesperada, mas um homem alto vestindo uma armadura se levantou e argumentou: “Sua Alteza, isso não é uma piada! "",""The members of the nobility who sat with him maintained their equanimity at his unexpected statement, but a tall man wearing a suit of armor stood up and argued, “Your Highness, this isn’t a joke!"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Todas as bruxas conhecidas devem ser mortas imediatamente após serem identificadas, ou outras bruxas podem ser tentadas a tentar salvá-la! "",""All known witches should be put to death immediately upon being identified, or other witches might be tempted to try and save her!"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Você quer forçar a igreja a se envolver quando souberem que permitimos que uma bruxa vivesse? "",""Do you want to force the church to get involved when they hear that we have allowed a witch to live?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Não temos escolha neste assunto!”\n"",""We have no choice in this matter!”\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Carter, este homem arrojado, era na verdade seu Cavaleiro Comandante. "",""Carter, this dashing man, was actually his Knight Commander."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Cheng Yan franziu a testa e disse: “Por quê? "",""Cheng Yan frowned and said, “Why?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Você está assustado?\"" "",""Are you scared?”"",null,null,1],[""Sua voz estava cheia de zombaria flagrante e não era um ato completo. "",""His voice was full of blatant mockery and wasn’t a complete act."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Um homem com um braço mais grosso que a cintura da chamada “bruxa” realmente temia uma batida na prisão por parte das mulheres. "",""A man with an arm thicker than the waist of the so called “witch” actually feared a prison raid from women."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""As bruxas eram realmente as mensageiras do diabo? "",""Were witches really the devil’s messengers?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""“Não seria melhor pegar mais bruxas do que se contentar com apenas uma?”\n"",""“Wouldn’t it be better to catch more witches than to settle for only one?”\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Ao vê-lo não mais pronunciar uma palavra, Cheng Yan acenou com a mão para chamar seus guardas pessoais e saiu. "",""Seeing him no longer utter a word, Cheng Yan waved his hand to call his personal guards and left."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Carter hesitou um momento antes de descer e alcançar as tropas que caminhavam ao lado do 4º príncipe. "",""Carter hesitated a moment before going down and catching up with the troops walking by the 4th prince’s side."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Os outros nobres se levantaram e prestaram homenagem ao príncipe, mas Cheng Yan podia ver indisfarçável desprezo nos olhos da multidão.\n"",""The other nobles got up and paid their respects to the prince, but Cheng Yan could see undisguised contempt from the eyes of those in the crowd.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""De volta à fortaleza, o castelo estava localizado ao sul da cidade fronteiriça, ele dispensou o ansioso Ministro Barov do lado de fora da porta de seus aposentos, permitindo-lhe finalmente dar um suspiro de alívio agora que estava sozinho.\n"",""Back in the keep, the castle was located to the south of the border town, he dismissed the anxious Minister Barov outside the door to his chambers, allowing him to finally breathe a sigh of relief now that he was alone.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Como uma pessoa que passou noventa por cento de seu tempo lidando com as pessoas através de um computador, encarando todos como se já tivesse ultrapassado sua zona de conforto. "",""As a person who’d spent ninety percent of his time dealing with people through a computer, facing everyone like he just had already surpassed his comfort zone."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Cheng Yan encontrou a localização de seu quarto a partir de suas novas memórias, sentou-se em sua cama e teve um momento de descanso real enquanto tentava suprimir seu coração batendo violentamente. "",""Cheng Yan found the location of his bedroom from his new memories, took a seat on his bed, and got a moment of real rest as he tried to suppress his violently beating heart."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""No momento, o mais importante era esclarecer a situação. "",""At the moment, the most important matter was to clarify the situation."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Por que o príncipe, que não podia ficar em Wimbledon City, a capital do reino, foi enviado para esta terra árida?\n"",""Why was the prince, who couldn’t stay in Wimbledon City, the capital of the kingdom, sent to this barren land?\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""A resposta inesperada que ele deu o deixou estupefato.\n"",""The unexpected answer he came up with left him stupefied.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Roland Wimbledon foi realmente enviado aqui para lutar pelo direito de suceder o rei.\n"",""Roland Wimbledon was actually sent here to fight for the right to succeed the king.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Tudo se originou da maravilhosa proclamação do rei Wimbledon III de Graycastle a seus filhos dizendo: “Você quer herdar o reino? "",""Everything had originated from King Wimbledon III of Graycastle’s wonderful proclamation to his children saying, “You want to inherit the kingdom?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""O príncipe primogênito não tem necessariamente o direito de se tornar rei, apenas a pessoa que se mostrar mais capaz de governar pode herdar o país.” "",""The first-born prince doesn’t necessarily have the right to become king, only the person who proves themselves as the most capable of governing can inherit the country.”"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele colocou vários territórios sob o domínio de seus cinco filhos e, após cinco anos, decidiria quem se tornaria seu sucessor com base no nível de habilidade que demonstrassem em governar seus respectivos territórios.\n"",""He placed various territories under the rule of his five children, and after five years he’d decide who would become his successor based on the level of skill they displayed in governing their respective territories.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Embora transformar a decisão de quem deve herdar o trono em uma meritocracia e fornecer oportunidades iguais independentemente do gênero possa soar como conceitos muito esclarecidos, o problema real era com a implementação real de tais ideias. "",""While turning the decision of who should inherit the throne into a meritocracy and providing equal opportunity regardless of gender might sound like very enlightened concepts, the real problem was with the actual implementation of said ideas."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Haveria alguma garantia de que todos os cinco receberam as mesmas condições iniciais? "",""Would there be any guarantee that all five of them received the same starting conditions?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Não era como jogar um jogo de estratégia em tempo real. "",""This wasn’t like playing a real-time strategy game."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Pelo que sabia, o segundo filho havia recebido um território melhor do que esta cidade fronteiriça. "",""To his knowledge, the second son had been given a better territory than this border town."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Na verdade, quando ele pensou sobre isso, parecia que entre as cinco regiões que haviam recebido, nenhuma das outras era pior do que sua cidade fronteiriça. "",""Actually when he thought about it, it seemed that among the five regions they’d been given, none of the others were worse than his frontier town."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Seu ponto de partida era simplesmente inferior.\n"",""His starting point was simply inferior.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Além disso, Cheng Yan se perguntou, como avaliar o nível de governança? "",""Also, Cheng Yan wondered, how was one to assess the level of governance?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Pela população? "",""By the population?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Poder militar? "",""Military power?"",null,null,1],[""Situação econômica? "",""Economic standing?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Wimbledon III não mencionou nenhum padrão, nem colocou as menores restrições em seus métodos de competição. "",""Wimbledon III hadn’t mentioned any standard, nor did he put the slightest restrictions on their methods of competition."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Caso alguém assassinasse secretamente os outros candidatos, o que ele faria? "",""In case someone secretly assassinated the other candidates, what would he do?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""A rainha ficaria parada assistindo seus filhos se matarem? "",""Would the queen stand by and watch her children kill each other?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Espere. "",""Wait."",null,null,10],[""…… Ele cuidadosamente recordou a próxima memória, tudo bem, outra má notícia; "",""…… He carefully recalled the next memory, all right, another piece of bad news;"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""a rainha morrera cinco anos antes.\n"",""the Queen had died five years ago.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan suspirou. "",""Cheng Yan sighed."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Obviamente, esta era uma era feudal bárbara e sombria em que ele se encontrava. Apenas a maneira como eles pareciam matar bruxas arbitrariamente foi o suficiente para lhe dar algumas dicas. "",""Obviously, this was a barbaric and dark feudal era he had found himself in. Just the way they seemed to wantonly kill witches was enough to give him a few hints."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Além disso, Cheng Yan pensou, por que ele iria querer se tornar rei? "",""Also, Cheng Yan thought, why would he want to become king?"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Sem internet e sem os confortos da civilização moderna, ele teria que viver a mesma vida que os nativos. "",""With no internet and none of the comforts of modern civilization, he’d have to live the same life as the native people."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Queimando bruxas para se divertir, vivendo em uma cidade onde todos jogavam seus excrementos onde quisessem e, finalmente, morrendo de peste negra.\n"",""Burning witches for fun, living in a city where everyone dumped their excrement wherever they wished, and finally dying from the Black Death.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan sendo um príncipe já pode ser considerado um ponto de partida muito alto. "",""Cheng Yan being a prince could already be considered a very high starting point."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Mesmo que ele não se tornasse rei, ele ainda tinha sangue real e já havia sido nomeado cavaleiro. "",""Even if he didn’t become king he was still of royal blood and had already been knighted."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Enquanto ele conseguisse permanecer vivo, ele seria considerado um dos Lordes do Reino.\n"",""As long as he managed to stay alive he would be considered as one of the Lords of the Realm.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Cheng Yan suprimiu seus pensamentos errantes e foi até o espelho do quarto. "",""Cheng Yan suppressed his wandering thoughts and went to his bedroom mirror."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""O homem que o olhava no espelho tinha cabelo grisalho claro, que era a característica mais marcante da família real. "",""The man looking back at him in the mirror had light gray hair, which was the royal family’s most distinctive feature."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Seu rosto estava ligeiramente pálido e com suas características faciais regulares, ele parecia estar completamente sem traços de personalidade. "",""His face was slightly pale and with his regular facial features, he seemed to be completely without personality traits."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele parecia carente de exercícios físicos e, quanto ao vinho e à mulher, lembrava-se de se entregar a ambos com certa regularidade. "",""He appeared to be lacking in physical exercise and as for wine and woman, he recalled indulging in both with some regularity."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Ele teve vários amantes na Cidade do Rei, mas todos foram participantes voluntários, ele não forçou ninguém.\n"",""He had had several lovers in the King’s City, but all had been willing participants, he hadn’t forced anyone.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""Quanto à causa de sua própria passagem... Cheng Yan adivinhou que, graças ao desejo desumano da empresa de progredir, seu chefe providenciou para que ele trabalhasse horas extras, o que por sua vez levou à tragédia que foi sua morte repentina. "",""As for the cause of his own crossing over… Cheng Yan guessed that thanks to the company’s inhuman urging to progress forward, his boss had arranged for him to work overtime, which in turn actually led to the tragedy that was his sudden death."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""As vítimas de casos como esses geralmente eram codificadores, engenheiros mecânicos e programadores.\n"",""The victims of cases like these were usually coders, mechanical engineers, and programmers.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""No final, não importa o que aconteça, pelo menos ganhei o equivalente a uma vida extra. "",""In the end, no matter what, at least I got the equivalent of an extra life."",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""Eu realmente não deveria reclamar muito, nos próximos dias, eu posso melhorar esta vida lentamente, mas minha primeira tarefa é jogar um 4º Príncipe convincente, para que outras pessoas não encontrem algo errado com meu comportamento e "",""I really shouldn't complain too much, in the coming days, I might be able to slowly improve this life, but my first task is to play a convincing 4th Prince, so that other people don't find something amiss with my behavior and"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]],[""acho que estou possuído pelo demônio, levando-me a ser queimado na fogueira, Cheng Yan pensou consigo mesmo.\n"",""think I'm possessed by the devil, leading to my being burned at the stake, Cheng Yan thought to himself.\n"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]],[null,true]]],[""\""Então, para viver bem ...\"" Cheng Yan respirou fundo, olhou no espelho e sussurrou: \""de agora em diante, sou Roland.\"""",""“So, in order to live well…” Cheng Yan took a deep breath, looked in the mirror, and whispered, “from now on, I’m Roland.”"",null,null,3,null,null,[[]],[[[""5a1e38863c8f79e4e0aed761f5d88a10"",""en_pt_2022q2.md""]]]]],null,""en"",null,null,null,null,[]]";

